sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      g_form.setVisible('u_other',false);
      return;
   }

   //Type appropriate comment here, and begin script below
	if(newValue == 4)
		{
			g_form.setVisible('u_other',true);
			g_form.setMandatory('u_other',true);
			g_form.setReadOnly('u_other',false);
			g_form.showFieldMsg('u_other','please specify','info');
		}
	else
		{
			g_form.setReadOnly('u_other',true);
			g_form.setMandatory('u_other',false);
			g_form.hideFieldMsg('u_other');
		}

}
