sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	if(newValue == 'andhra pradesh')
		{
			g_form.clearOptions('u_college_name');
			g_form.addOption('u_college_name',1,'jntua');
			g_form.addOption('u_college_name',2,'PBR Vits');
			g_form.addOption('u_college_name',3,'RSR College');
			g_form.addOption('u_college_name',4,'Sv University');
			g_form.addOption('u_college_name',5,'Shimhapuri');
			g_form.addOption('u_college_name',6,'Other');
		}
	if(newValue == 'telangana')
		{
			g_form.clearOptions('u_college_name');
			g_form.addOption('u_college_name',1,'JntuH');
			g_form.addOption('u_college_name',2,'hyderbad university');
			g_form.addOption('u_college_name',3,'Warangal university');
			g_form.addOption('u_college_name',4,'Other');
		}
	if(newValue == 'maharastra')
		{
			g_form.clearOptions('u_college_name');
			g_form.addOption('u_college_name',1,'mumbai university');
			g_form.addOption('u_college_name',2,'pune university');
			g_form.addOption('u_college_name',3,'shiridi university');
			g_form.addOption('u_college_name',4,'Other');
		}


}
